mapper.update:
UPDATE T_SEG_PERFIL
SET
    perfil_v_funcion = #{funcion}, 
    perfil_v_descripcion = #{descripcion}, 
    area_n_codigo = #{area}
WHERE
    perfil_n_codigo = #{codigo}
AND
    empresa_n_codigo = #{empresa}

mapper.delete:
DELETE FROM
   T_SEG_PERFIL
WHERE
      perfil_n_codigo = #{codigo}
  AND
      empresa_n_codigo = #{empresa}
