mapper.select:
SELECT cat.categoria_n_codigo as codigo, cat.empresa_n_codigo as empresa, cat.categoria_v_nombre as nombre, cat.categoria_b_estado as estado, cat.categoria_n_codigo_p as codigoPadre FROM T_NEG_CATEGORIA cat
WHERE cat.empresa_n_codigo = 1 OR cat.empresa_n_codigo = #{empresa}
ORDER BY cat.categoria_v_nombre

mapper.update:
UPDATE T_NEG_CATEGORIA
SET
categoria_v_nombre = #{nombre},
categoria_n_codigo_p = #{codigoPadre, jdbcType=INTEGER},
categoria_b_estado = #{estado}
WHERE
categoria_n_codigo = #{codigo}
AND
empresa_n_codigo = #{empresa}
