mapper.select:
SELECT art.id as productId, art.empresaid as productEmpresaId, art.nombre as productName, art.descripcion as productDescription, art.activo as productActivo, art.imagen as nomimg, cart.id as catProdId, cart.categoryid as categoryId, cart.productid as productId, cat.id as categoryId, cat.empresaid as categoryEmpresaId, cat.nombre as categoryName, cat.estado as categoryEstado, cat.parentid as categoryParentId FROM T_NEG_ARTICULO art, T_NEG_CATEG2ARTIC cart, T_NEG_CATEGORIA cat
art.id = cart.productid AND cart.categoryid = cat.id AND art.empresaid = #{empresa} AND cart.categoryid in
#{id, jdbcType=INTEGER}
AND art.nombre LIKE %#{nombre}%
AND art.activo = #{activo}

mapper.update:
UPDATE T_NEG_ARTICULO
SET
    articulo_n_stock = #{stock}
WHERE
    empresa_n_codigo = #{empresa}
AND
    articulo_n_codigo = #{codigo}
