mapper.select:
SELECT cat.id as id, cat.empresaid as empresa, cat.nombre as categoryName, cat.estado as estado, cat.parentid as categoryParentId FROM T_NEG_CATEGORIA cat
WHERE cat.empresaId = 1 OR cat.empresaId = #{empresa}
ORDER BY cat.nombre

mapper.update:
UPDATE T_NEG_CATEGORIA
SET
nombre = #{categoryName},
parentid = #{categoryParentId, jdbcType=INTEGER},
estado = #{estado}
WHERE
id = #{id}
AND
empresaId = #{empresa}
