mapper.select:
SELECT emp.id as employeeId, emp.companyid as companyId, emp.name as employeeName, emp.birthday as birthday, emp.lastname as employeeLastName, emp.lastname2 as employeeLastName2, emp.doctypeid as documentTypeId, emp.docnumber as documentNumber, emp.address as address, emp.phone as phone, emp.cellphone as cellPhone, emp.email as email, emp.city as city, emp.ubigeo as ubigeo, emp.sexid as sexId, emp.civilstateid as civilStateId, emp.active as active, us.id as userId, us.username as username, us.companyid as companyId, us.password as password, rol.id as roleId, rol.name as roleName, rol.description as roleDescription, rol.areaid as roleAreaId, area.id areaId, area.name as areaName FROM T_BUS_EMPLOYEE emp LEFT JOIN T_SEG_USER us ON emp.userid = us.id LEFT JOIN T_SEG_ROLE rol ON us.roleid = rol.id LEFT JOIN T_COM_AREA area ON rol.areaid = area.id WHERE emp.companyid = #{companyId}
AND emp.userid = #{userId}
AND emp.id = #{id}
AND emp.lastname LIKE '%' || #{employeeLastName} || '%'
AND emp.lastname2 LIKE '%' || #{employeeLastName2} || '%'
AND emp.name LIKE #{patternEmpName}
AND emp.doctypeid = #{documentTypeId} AND emp.docnumber = #{documentNumber}

mapper.delete:
DELETE FROM
   T_BUS_EMPLOYEE
WHERE
   companyid = #{companyId}
AND
   id = #{id}
