mapper.select:
SELECT mar.id as id, mar.companyid as company, mar.nombre as marcaName, mar.activo as active, mar.imagen as imageName FROM T_BUS_TRADEMARK mar
mar.companyId = #{company}
ORDER BY mar.nombre

mapper.update:
UPDATE T_BUS_TRADEMARK
SET
    nombre = #{marcaName},
    activo = #{active},
    imagen = #{imageName}
WHERE
    id = #{id}
AND
    companyId = #{company}
