mapper.select:
SELECT mar.id as id, mar.empresaid as empresa, mar.nombre as marcaName, mar.activo as active, mar.imagen as imageName FROM T_NEG_MARCA mar
mar.empresaId = #{empresa}
ORDER BY mar.nombre

mapper.update:
UPDATE T_NEG_MARCA
SET
    nombre = #{marcaName},
    activo = #{active},
    imagen = #{imageName}
WHERE
    id = #{id}
AND
    empresaId = #{empresa}
