mapper.select:
SELECT rol.id as roleId, rol.name as roleName, rol.description as roleDescription, rol.companyid as companyId, rolMod.id roleModuId, rolMod.active roleModuActive, modu.id as moduId, modu.description as moduDescription, modu.resource as moduResource, area.id areaId, area.name as areaName FROM T_SEG_ROLE rol LEFT JOIN T_COM_AREA area ON rol.areaid = area.id LEFT JOIN T_SEG_ROLE2MODULE rolMod ON rol.id = rolMod.roleid LEFT JOIN T_SEG_MODULE modu ON rolMod.moduleid = modu.id
rol.companyid = #{companyId} AND rol.areaid = #{areaId}
AND rol.id in
#{id, jdbcType=INTEGER}

mapper.update:
UPDATE T_SEG_ROLE
SET
    name = #{roleName},
    description = #{roleDescription},
    areaid = #{areaId}
WHERE
    id = #{id}
AND
    companyid = #{companyId}

mapper.delete:
DELETE FROM
   T_SEG_ROLE
WHERE
      id = #{id}
  AND
      companyid = #{companyId}
