mapper.select:
SELECT mar.id as id, mar.companyid as company, mar.name as tradeMarkName, mar.active as active, mar.image as imageName FROM T_BUS_TRADEMARK mar
mar.companyId = #{company}
ORDER BY mar.name

mapper.update:
UPDATE T_BUS_TRADEMARK
SET
    name = #{tradeMarkName},
    active = #{active},
    image = #{imageName}
WHERE
    id = #{id}
AND
    companyId = #{company}
