mapper.select:
SELECT cat.id as id, cat.companyid as company, cat.nombre as categoryName, cat.estado as estado, cat.parentid as categoryParentId FROM T_BUS_CATEGORY cat
WHERE cat.companyId = 1 OR cat.companyId = #{company}
ORDER BY cat.nombre

mapper.update:
UPDATE T_BUS_CATEGORY
SET
nombre = #{categoryName},
parentid = #{categoryParentId, jdbcType=INTEGER},
estado = #{estado}
WHERE
id = #{id}
AND
companyId = #{company}
