mapper.select:
SELECT us.id as userId, us.username as username, us.companyid as companyId, rol.id as roleId, rol.name as roleName, rol.description as roleDescription, rol.areaid as roleAreaId, area.id areaId, area.name as areaName FROM T_SEG_USER us LEFT JOIN T_SEG_ROLE rol ON us.roleid = rol.id LEFT JOIN T_COM_AREA area ON rol.areaid = area.id
us.companyid = #{companyId} AND us.id = #{id}
ORDER BY us.id

mapper.update:
UPDATE T_SEG_USER
SET
    username = #{username},
    roleid = #{roleId}
WHERE
    id = #{id}
AND
    companyid = #{companyId}

UPDATE T_SEG_USER
SET
    password = #{password}
WHERE
    username = #{username}
AND
    companyid = #{companyId}
